pragma solidity ^0.4.21;

// ERC20 contract interface
contract Token {
  function balanceOf(address) public view returns (uint);

  function allowance(address, address) public view returns (uint);
}


contract AllowancesAndBalancesChecker {
  /* Fallback function, don't accept any ETH */
  function() public payable {
    revert();
  }

  /*
    Checks if there is token balance and allowance for a 
    specific amount and given wallet address

    Returns the following to avoid possible errors:
      - return 4 on non-contract address 
      - returns 4 if the contract doesn't implement balanceOf

    Returns
      - 0 for No Allowance, No Balance
      - 1 for No Allowance, Balance
      - 2 for Allowance, No Balance
      - 3 for Allowance, Balance
  */
  function allowanceAndBalanceCheck(address user, address token, address spender, uint amount) public view returns (uint) {
    // check if token is actually a contract
    uint256 tokenCode;
    assembly { tokenCode := extcodesize(token) } // contract code size

    // is it a contract and does it implement balanceOf 
    if (tokenCode > 0 && token.call(bytes4(0x70a08231), user)) {  
      uint256 allowance = Token(token).allowance (user, spender);
      uint256 balance = Token(token).balanceOf(user);
      if(allowance >= amount && balance >=  amount){
        return 3;
      }
      if(allowance >= amount && balance < amount){
        return 2;
      }
      if(allowance < amount && balance >= amount){
        return 1;
      }
      if(allowance < amount && balance < amount){
        return 0;
      }
    } else {
      return 4;
    }
  }

  /*
    Checks if the given wallet addresses have allowance and balance
    for specific token address and amount

    Possible error throws:
      - extremely large arrays for user and or tokens (gas cost too high) 

    Returns a one-dimensional that's users.length long. Each element
    of the array represent a value if the allowance and the balance is available
    for a given wallet address, token addres, spender, amount
  */
  function allowancesAndBalancesResult(address[] users, address[] tokens, address spender, uint[] amounts) external view returns ( uint []) {
    uint[] memory result  = new uint[](users.length);

    for(uint i = 0; i < users.length; i++) {
        if (tokens[i] != address(0x0)) { 
          result[i] = allowanceAndBalanceCheck(users[i], tokens[i], spender, amounts[i]);
        } else {
          result[i] = 4; 
        }

    }

    return result;
  }

}
